    Teacher teacher = new Teacher();
    List<Teacher> teachers = new List<Teacher>();
    DateTime? bday = DateTime.Today;

    Enums.Gender gender { get; set; }
    MudMessageBox mbox { get; set; }
    MudMessageBox dbox { get; set; }

    string searchString = "";
    bool open = false;
